#include <iostream>#include <string>using namespace std;#if 0int getTotalcount(int month) {	if (month == 1 || month == 2) {		return 1;	}	if (month >= 3) {		return getTotalcount(month - 1) + getTotalcount(month - 2);	}}int main() {	int month;	cin >> month;	cout << getTotalcount(month) << endl;	system("pause");	return 0;}#endif#if 1bool isMatch(const char* s1, const char* s2) {	if (*s1 == '\0' && *s2 == '\0') {		return true;	}	if (*s1 == '\0' || *s2 == '\0') {		return false;	}	if (*s1 == '?') {		return isMatch(s1 + 1, s2 + 1);	}	else if (*s1 == '*') {		return isMatch(s1 + 1, s2) || isMatch(s1 + 1, s2 + 1) || isMatch(s1, s2 + 1);	}	else if (*s1 == *s2) {		return isMatch(s1+1,s2+1);	}	else {		return false;	}}int main() {	string s1, s2;	cin >> s1 >> s2;	if (isMatch(s1.c_str(), s2.c_str())) {		cout << "true" << endl;	}	else {		cout << "false" << endl;	}	system("pause");	return 0;}#endif